pub enum Error {
    UsualCase {
        backtrace: Backtrace,
    },
    UsedInTightLoop {
        backtrace: Option<Backtrace>,
    },
    SnafuErrorAsSource {
        source: ConfigFileError,
    },
    SourceErrorDoesNotHaveBacktrace {
        source: std::io::Error,
        backtrace: Backtrace,
    },
}

pub enum ConfigFileError {
    Dummy {
        backtrace: Backtrace,
    },
}

pub enum Error {
    Leaf {
        user_id: i32,
    },
    Intermediate {
        source: std::io::Error,
    },
}
